import React from 'react';
import { Header, Subheader, Upload } from './components';
import './App.css';

const baseUrl = "https://reqres.in/api/users?page=1"

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
        datasets: []
    }

    this.addDataset = this.addDataset.bind(this)
    this.deleteDataset = this.deleteDataset.bind(this)
  }
  render() {
    return (
      <div>
        <Header />
        <Subheader />
        <Upload />
      </div>
    )
  }

  deleteDataset(id) {
    this.setState({
        files: this.state.files.filter((el)=>el.id !== id)
    })
}

addDataset(dataset) {
    const id = this.state.datasets.length + 1
    this.setState({datasets: [...this.state.datasets, {id, ...dataset}]})
}
}

export default App
