import React from 'react'
import Input from '../Input/Input'
import Back from '../Back/Back'
import UploadFile from './UploadFilesSection/UploadFilesPart'
import './Upload.css'

class Upload extends React.Component {
    constructor(props) {
        super(props)

        this.addFile = this.addFile.bind(this)
        this.deleteFile = this.deleteFile.bind(this)
    }
    render() {
        return (
            <div className='upload'>
                <Back />
                <UploadFile limitFile="5" limitImage="100" />
                <Input label="Название" placeholder="Введите название"/>
                <div className='metadataLabel'>Видимость</div>
                <select id="Visible">
                    <option value="Private" selected>Приватный</option>
                    <option value="Public">Публичный</option>
                </select>
                <Input label="Авторы" placeholder="Введите название"/>
                <Input label="Источник" placeholder="Введите название"/>
                <Input label="Частота обновлений" placeholder="Введите название"/>
            </div>
        ) 
    }

    deleteFile(id) {
        this.setState({
            files: this.state.files.filter((el)=>el.id !== id)
        })
    }

    addFile(file) {
        const id = this.state.files.length + 1
        this.setState({files: [...this.state.files, {id, ...file}]})
    }
}

export default Upload
